// Merge the classification result with the helpline database
const classification = $('1. Understand - Classify Crime').first().json;
const helplines = $('Fetch Helpline Database').all().map(item => item.json);
const webhookData = $('Webhook Trigger').first().json.body;

// Find matching platform helpline
const platform = webhookData.paymentPlatform || '';
const matchedHelplines = helplines.filter(h => {
  const bankName = (h['Bank/Platform'] || '').toLowerCase();
  const platLower = platform.toLowerCase();
  return platLower.includes(bankName) || bankName.includes(platLower.split(' ')[0]);
});

// Always include national helplines
const nationalHelplines = helplines.filter(h => {
  const name = (h['Bank/Platform'] || '').toLowerCase();
  return name.includes('national') || name.includes('rbi') || name.includes('police') || name.includes('women');
});

const relevantHelplines = [...matchedHelplines, ...nationalHelplines];
const chargebackWindow = matchedHelplines.length > 0 ? matchedHelplines[0]['Chargeback Window (Hours)'] : '72';

return [{
  json: {
    classification,
    webhookData,
    relevantHelplines,
    chargebackWindow,
    allHelplines: helplines
  }
}];

