// Assemble the final response with REAL data enrichment
const parseGroq = (node) => {
  try {
    const text = node.choices[0].message.content;
    const cleaned = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    return JSON.parse(cleaned);
  } catch (e) {
    return null;
  }
};

const mergedData = $('Merge Real Data + Classification').first().json;
const classify = parseGroq(mergedData.classification);
const evidence = parseGroq($('2. Gather - Evidence Checklist').first().json);
const legal = parseGroq($('3. Protect - Legal Interpretation').first().json);
const recovery = parseGroq($('4. Empower - Recovery Probability').first().json);
const complaint = parseGroq($('5. Act - Complaint & Action Plan').first().json);

return [{
  json: {
    whatHappened: classify || { crimeType: 'Cybercrime', summary: 'We were unable to classify this precisely, but your report has been noted.', reassurance: 'Please still follow the action plan below.' },
    evidenceChecklist: Array.isArray(evidence) ? evidence : [],
    legalProtections: Array.isArray(legal) ? legal : [],
    recoveryWindow: recovery || { percentage: 50, message: 'Act quickly for the best chances.', hoursRemaining: 24, encouragement: 'Every step helps.' },
    actionPlan: complaint?.actionPlan || [],
    complaintDraft: complaint?.complaintDraft || 'Unable to generate complaint draft. Please visit cybercrime.gov.in directly.',
    // REAL DATA passed through to frontend
    helplines: mergedData.relevantHelplines,
    chargebackWindowHours: mergedData.chargebackWindow
  }
}];

